import { useRef, useEffect } from "react";
import Card from "../../../../UI/Card/Card";
import Title from "../../../../UI/Title/Title";
import SubTitle from "../../../../UI/SubTitle/SubTitle";
import style from "./AccountSmallChart.module.css";
import Chart from "chart.js/auto";

function AccountSmallChart(props) {
  return (
    <Card className={style.accountSmallChart}>
      <Title className={style.title}>know more about your expense</Title>
      <SubTitle>choose data</SubTitle>
      <select>
        <option>which item do I expense the most</option>
        <option>which day do I expense the most</option>
        <option>which month do I expense the most</option>
      </select>
    </Card>
  );
}

export default AccountSmallChart;

