import { AiFillHome } from "react-icons/ai";
import { FaRegCalendarAlt } from "react-icons/fa";
import { AiOutlineBarChart } from "react-icons/ai";
import { FaSearch } from "react-icons/fa";
import { AiFillSetting } from "react-icons/ai";
import { MdAccountCircle } from "react-icons/md";
import { SiCashapp } from "react-icons/si";
import SideBarItem from "./SideBarItem";
import style from "./SideBar.module.css";

const SIDEBAR__ICON = [
  [
    "Home",
    <AiFillHome
      aria-label="home"
      className={`${style["sidebar__icon"]} transition--25`}
    />,
  ],
  [
    "Calendar",
    <FaRegCalendarAlt
      aria-label="calendar"
      className={`${style["sidebar__icon"]} transition--25`}
    />,
  ],
  [
    "Chart",
    <AiOutlineBarChart
      aria-label="chart"
      className={`${style["sidebar__icon"]} transition--25`}
    />,
  ],
  [
    "Search",
    <FaSearch
      aria-label="search"
      className={`${style["sidebar__icon"]} transition--25`}
    />,
  ],
  [
    "Account",
    <MdAccountCircle
      aria-label="account"
      className={`${style["sidebar__icon"]} transition--25`}
    />,
  ],
  [
    "Setting",
    <AiFillSetting
      aria-label="setting"
      className={`${style["sidebar__icon"]} transition--25`}
    />,
  ],
];

const dateOptObj = { month: "short" };
const dateOptObj1 = { year: "numeric" };

function SideBar(props) {
  // SIDEBAR__ICON is 2D array
  const sidebarItem = SIDEBAR__ICON.map(([title, icon], index) => {
    let activePage = false;
    if (props.page - 0 === index) activePage = true;

    return (
      <SideBarItem
        title={title}
        key={title}
        pageIndex={index}
        activePage={activePage}
        menuClickHandler={props.menuClickHandler}
        setPage={props.setPage}
      >
        {icon}
      </SideBarItem>
    );
  });

  return (
    <aside
      className={`${style.sidebar} ${
        props.showSidebar ? `${style["sidebar--show"]}` : ""
      }`}
    >
      <div>
        <a href="." aria-label="reload the page">
          <SiCashapp aria-label="reload the page" className={style.logo} />
        </a>

        <nav>
          <ul className={style["sidebar__item"]}>{sidebarItem}</ul>
        </nav>
      </div>

      <div className={style["sidebar__info"]}>
        <p>{`${props.today.getDate()} th`}</p>
        <p>
          {new Intl.DateTimeFormat("en-US", dateOptObj).format(props.today)}
        </p>
        <p>
          {new Intl.DateTimeFormat("en-US", dateOptObj1).format(props.today)}
        </p>
        <img src="data:image/svg+xml,%3Csvg%20stroke%3D%22currentColor%22%20fill%3D%22currentColor%22%20stroke-width%3D%220%22%20viewBox%3D%220%200%2024%2024%22%20height%3D%221em%22%20width%3D%221em%22%20xmlns%3D%22http%3A//www.w3.org/2000/svg%22%3E%3Cg%3E%3Cpath%20fill%3D%22none%22%20d%3D%22M0%200h24v24H0z%22%3E%3C/path%3E%3Cpath%20fill-rule%3D%22nonzero%22%20d%3D%22M11.5%202a9.5%209.5%200%200%201%209.5%209.5%209.46%209.46%200%200%201-1.003%204.254l2.463%202.464a1%201%200%200%201%200%201.414l-2.828%202.828a1%201%200%200%201-1.414%200l-2.464-2.463A9.46%209.46%200%200%201%2011.5%2021a9.5%209.5%200%200%201%200-19zm5.303%2013.388l-1.414%201.414%203.536%203.535%201.414-1.414-3.536-3.535zm1.864-6.105l-9.384%209.384c.7.216%201.445.333%202.217.333a7.48%207.48%200%200%200%202.74-.516l-.972-.974a1%201%200%200%201%200-1.414l2.828-2.828a1%201%200%200%201%201.414%200l.974.972A7.48%207.48%200%200%200%2019%2011.5c0-.772-.117-1.516-.333-2.217z%22%3E%3C/path%3E%3C/g%3E%3C/svg%3E"></img>
      </div>
    </aside>
  );
}

export default SideBar;

