import { useContext, useState, useEffect, useReducer } from "react";
import InputRadio from "../InputRadio/InputRadio";
import Button from "../Button";
import Modal from "../Modal/Modal";
import ExpenseDataContext from "../../../store/expenseData/expenseData--context";
import { v4 as uuidv4 } from "uuid";

import style from "./AddDataForm.module.css";

const EXPENSE_CATEGORY = {
  food: ["breakfast", "brunch", "lunch", "dinner", "snack", "drink"],
  clothing: ["clothes", "pants", "shoes", "accessories", "underwear"],
  housing: [
    "rent",
    "water  bill",
    "electricity bill",
    "gas bill",
    "cable bill",
    "internet bill",
  ],
  transportation: ["gasoline", "parking", "maintenance"],
  education: ["books", "course"],
  entertainment: ["video games", "computer games", "party"],
};

const INCOME_CATEGORY = {
  salary: ["salary", "bonus"],
  stock: ["stock"],
};

function reducer(state, action) {
  switch (action.type) {
    case "CATEGORT_CHANGE":
      {
        let mainCategory, subCategory;
        if (action.value) {
          mainCategory = Object.keys(EXPENSE_CATEGORY);
          subCategory = EXPENSE_CATEGORY["food"];
        }

        return { ...state, category: action.value, mainCategory, subCategory };
      }

      break;

    default:
      break;
  }
}

function AddDataForm(props) {
  const initialObj = {
    category: "income",
    mainCategory: Object.keys(EXPENSE_CATEGORY),
    subCategory: EXPENSE_CATEGORY["food"],
    date: "",
    description: "",
    price: "",
    isValid: false,
  };
  const [formData, formDataDispatch] = useReducer(reducer, initialObj);
  const [animation, setAnimation] = useState(false);
  const { addExpenseData } = useContext(ExpenseDataContext);

  // const [categoryState, setCategoryState] = useState("expense");
  // const [mainCategoryState, setMainCategoryState] = useState(
  //   Object.keys(EXPENSE_CATEGORY)
  // );
  // const [subCategoryState, setSubCategoryState] = useState(
  //   EXPENSE_CATEGORY["food"]
  // );

  // const [date, setDate] = useState();
  // const [isValid, setIsValid] = useState(false);

  useEffect(() => {
    setAnimation(formData.category);

    const time = setTimeout(() => {
      setAnimation(false);
    }, 500);

    return function cleanUp() {
      clearTimeout(time);
    };
  }, [formData.category]);

  // let mainCategoryArr = [];
  // if (categoryState === "expense")
  //   mainCategoryArr = Object.keys(EXPENSE_CATEGORY);
  // if (categoryState === "income")
  //   mainCategoryArr = Object.keys(INCOME_CATEGORY);

  function categoryChangeHandler(e) {
    formDataDispatch({ type: "CATEGORT_CHANGE", value: e.target.value });
    // setCategoryState(e.target.value);
    // if (e.target.value === "expense") {
    //   // setMainCategoryState(EXPENSE_CATEGORY["food"]);
    //   setMainCategoryState(Object.keys(EXPENSE_CATEGORY));
    //   setSubCategoryState(EXPENSE_CATEGORY["food"]);
    // }
    // if (e.target.value === "income") {
    //   // setMainCategoryState(INCOME_CATEGORY["salary"]);
    //   setMainCategoryState(Object.keys(INCOME_CATEGORY));
    //   setSubCategoryState(EXPENSE_CATEGORY["salary"]);
    // }
  }

  function mainCategoryChangeHandler(e) {
    // setMainCategoryState(e.target.value);
  }

  function subCategoryChangeHandler(e) {
    // if (categoryState === "expense")
    // setSubCategoryState(EXPENSE_CATEGORY[e.target.value]);
    // if (categoryState === "income")
    // setSubCategoryState(INCOME_CATEGORY[e.target.value]);
  }

  function dateChangeHandler(e) {
    // setDate(e.target.value);
  }

  function priceChangeHandler(e) {
    // if (
    //   e.target.value.length > 0 &&
    //   !Object.is(-0, Number(e.target.value)) &&
    //   Number(e.target.value) >= 0
    // )
    //   setIsValid(true);
    // else setIsValid(false);
  }

  function cancelClickHandler() {
    if (props.closeAddDataFormHandler) props.closeAddDataFormHandler();

    if (props.closeFormHandler) props.closeFormHandler();
  }

  function formSubmitHandler(e) {
    e.preventDefault();
    // const newData = {
    //   id: uuidv4(),
    //   category: categoryState,
    //   mainCate: mainCategoryState,
    //   subCate: "",
    // };
    // console.log("aa");
  }

  let classNameAnimation = !animation
    ? ""
    : animation === "expense"
    ? "bump--blue"
    : "bump--pink";
  return (
    <Modal>
      <form onSubmit={formSubmitHandler} className={style.form}>
        <div className={style["title__container"]}>
          <InputRadio
            classInput={style.input}
            classCheck={`${style.expense} ${style.check}`}
            classLabel={style.title}
            label="expense"
            name="title"
            value="expense"
            onChange={categoryChangeHandler}
            checked={categoryState === "expense"}
          />
          <InputRadio
            classInput={style.input}
            classCheck={`${style.income} ${style.check}`}
            classLabel={style.title}
            label="income"
            name="title"
            value="income"
            onChange={categoryChangeHandler}
            check={categoryState === "income"}
          />
        </div>
        <hr className={style.line}></hr>
        <div className={style["form__container"]}>
          <div className={style["container"]}>
            <label className={`${style.label} ${style[classNameAnimation]}`}>
              main category
            </label>
            <select
              onChange={subCategoryChangeHandler}
              className={style.input}
              type="select"
            >
              {mainCategoryState.map((element) => (
                <option value={element} key={element}>
                  {element}
                </option>
              ))}
            </select>
          </div>

          <div className={style["container"]}>
            <label className={`${style.label} ${style[classNameAnimation]}`}>
              sub category
            </label>

            <select className={style.input} type="select">
              {subCategoryState.map((element) => (
                <option key={element}>{element}</option>
              ))}
            </select>
          </div>
          <div className={style["container"]}>
            <label className={`${style.label} ${style[classNameAnimation]}`}>
              description (optional)
            </label>
            <input className={style.input} type="text"></input>
          </div>

          <div className={style["container"]}>
            <label className={`${style.label} ${style[classNameAnimation]}`}>
              date
            </label>
            <input
              className={style.input}
              type="date"
              onChange={dateChangeHandler}
              defaultValue={
                props.date === undefined
                  ? new Date().toISOString().substr(0, 10)
                  : new Date(props.date).toISOString().substr(0, 10)
              }
            ></input>
          </div>

          <div className={`${style["container"]} ${style["container--last"]}`}>
            <label className={`${style.label} ${style[classNameAnimation]}`}>
              price
            </label>
            <input
              onChange={priceChangeHandler}
              className={style.input}
              type="number"
            ></input>
          </div>

          <div className={`${style["btn__container"]} `}>
            <Button
              type="button"
              className={`${style.btn} ${style["btn--isValid"]}`}
              onClick={cancelClickHandler}
            >
              cancel
            </Button>
            <Button
              type="submit"
              className={
                isValid
                  ? `${style["btn--isValid"]} ${style.btn}`
                  : `${style.btn}`
              }
              disabled={!isValid}
            >
              add
            </Button>
          </div>
        </div>
      </form>
    </Modal>
  );
}

export default AddDataForm;
