import { useRef, useEffect, useState, useContext } from "react";
import InputRadio from "../../../UI/InputRadio/InputRadio";
import ExpenseDataContext from "../../../../store/expenseData/expenseData--context";
import createDateFormat from "../../../Others/createDateFormat";
import createFilteredData from "../../../Others/CreateChartData/createFilteredData";
import style from "./AccountChart.module.css";
import Chart from "chart.js/auto";
import createConfig from "../../../Others/CreateChartData/createConfig";

const chartRadioArr = ["1wk", "1mo", "3mo", "6mo", "1yr", "select date"];

const TODAY = new Date();
const initialDate = new Date(new Date().setDate(new Date().getDate() - 7));

function AccountChart(props) {
  const { expenseData } = useContext(ExpenseDataContext);
  const [chartData, setChartData] = useState(
    createChartDataArr(initialDate, TODAY, expenseData)
  );
  const chartRef = useRef(null);

  const [data, labels] = chartData;

  const config = {
    type: "line",
    data: {
      labels: labels,
      datasets: [
        {
          fill: {
            target: "origin",
          },
          label: "",
          data: data,
          backgroundColor: ["rgba(54, 162, 235, 0.2)"],
          borderColor: ["rgb(54, 162, 235)"],
          borderWidth: 1,
          pointBackgroundColor: ["rgb(54, 162, 235)"],
        },
      ],
    },
    options: {
      elements: {
        line: {
          tension: 0.5,
        },
      },
      scales: {
        y: {
          beginAtZero: true,
        },
      },
    },
  };

  useEffect(() => {
    const chart = new Chart(chartRef.current, config);

    return function cleanUp() {
      chart.destroy();
    };
  }, [chartData]);

  return (
    <div className={style["chart__container"]}>
      <div className={style.chart}>
        <canvas ref={chartRef}></canvas>
      </div>
    </div>
  );
}

export default AccountChart;
