import { useEffect, useState } from "react";
import Card from "../../../UI/Card/Card";
import Title from "../../../UI/Title/Title";
import SubTitle from "../../../UI/SubTitle/SubTitle";
import Button from "../../../UI/Button";
import style from "./AccountNews.module.css";
import { BiRefresh } from "react-icons/bi";

const newsArr = [];

function AccountNews(props) {
  const [news, setNews] = useState(0);

  useEffect(() => {
    async function getNews() {
      const data = await fetch(
        "https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=5295e93382ff4fd1a92c1256f5843d3f"
      );

      const res = await data.json();

      newsArr.push(...res.articles);

      console.log(newsArr);
    }

    getNews();
  }, []);

  let title, img, url, description, source;
  if (newsArr.length !== 0) {
    title = newsArr[news].title;
    img = newsArr[news].title;
    title = newsArr[news].title;
  }

  function refreshClickHandler() {}

  return (
    <Card className={style.card}>
      <div className={style["title__section"]}>
        <Title className={style.title}>latest news</Title>
        <Button>
          <BiRefresh className={style.refresh} />
        </Button>
      </div>
      <SubTitle className={style.subTitle}>
        "market rally at turning point; what to do now - investors' business
        daily"
      </SubTitle>
      <div className={style["img__container"]}>
        <img
          className={style.img}
          src="https://kubrick.htvapps.com/htv-prod-media.s3.amazonaws.com/images/california-budget-1585234231.jpg?crop=0.995xw:1.00xh;0.00321xw,0&resize=1200:*"
        />
      </div>
    </Card>
  );
}

export default AccountNews;
