import { useContext } from "react";
import ExpenseDataContext from "../../../store/expenseData/expenseData--context";
import DisplayThemeContext from "../../../store/displayTheme/displayTheme--context";
import Modal from "../Modal/Modal";
import AccountInfo from "../AccountInfo/AccountInfo";

function AccountInfoOverlay(props) {
  const { expenseData, categoryExpense } = useContext(ExpenseDataContext);
  const { displayTheme } = useContext(DisplayThemeContext);

  const [startingDateObj, endingDateObj] = createAccountCardPreData(props.type);

  const [accIncome, accExpense] = createAccAmount(
    expenseData,
    true,
    startingDateObj,
    endingDateObj
  );

  const net = accIncome - accExpense;

  return (
    <Modal>
      <AccountInfo
        title={props.type}
        income={accIncome}
        expense={props.accExpense}
        net={net}
      />
    </Modal>
  );
}

export default AccountInfoOverlay;
