import { useContext } from "react";
import Card from "../../../UI/Card/Card";
import SubTitle from "../../../UI/SubTitle/SubTitle";
import style from "./AccountCard.module.css";
import ExpenseDataContext from "../../../../store/expenseData/expenseData--context";

const TODAY = new Date();

function AccountCard() {
  const { expenseData } = useContext(ExpenseDataContext);

  const accIncome = expenseData
    .filter(
      (expenseData) =>
        expenseData.category === "income" &&
        Number(new Date(expenseData.time)) <= Number(TODAY)
    )
    .reduce((acc, cur) => acc + Number(cur.price), 0);

  const accExpense = expenseData
    .filter(
      (expenseData) =>
        expenseData.category === "expense" &&
        Number(new Date(expenseData.time)) <= Number(TODAY)
    )
    .reduce((acc, cur) => acc + Number(cur.price), 0);

  return (
    <div className={style["card__container"]}>
      <Card className={style.card}>
        <SubTitle className={style["card__text"]}>income</SubTitle>
        <SubTitle
          className={style["card__subtitle--number"]}
        >{`$${accIncome}`}</SubTitle>
      </Card>
      <Card className={style.card}>
        <SubTitle className={style["card__subtitle"]}>
          <p className={style["card__subtitle--text"]}>expense</p>
          <p className={style["card__subtitle--number"]}>{`$${accExpense}`}</p>
        </SubTitle>
      </Card>
      <Card className={style.card}>
        <SubTitle className={style["card__subtitle"]}>
          <p className={style["card__subtitle--text"]}>net</p>
          <p className={style["card__subtitle--number"]}>{`$${
            accIncome - accExpense
          }`}</p>
        </SubTitle>
      </Card>
    </div>
  );
}

export default AccountCard;
