import { useState } from "react";

import MainContent from "./components/MainContent/MainContent";
import SideBar from "./components/SideBar/SideBar";
import AddDataForm from "./components/UI/AddDataForm/AddDateForm";
import ExpenseListModal from "./components/UI/ExpenseListModal/ExpenseListModal";
import Modal from "./components/UI/Modal/Modal";
import ModalShownContext from "./store/modalShown/modalShown--context";
import ExpenseDataProvider from "./store/expenseData/ExpenseDataProvider";

import style from "./App.module.css";

const TODAY = new Date();

function App() {
  const [page, setPage] = useState(0);
  const [addDataFormModalShown, setAddDataFormModalShown] = useState(false);
  const [dateOnForm, setDateOnForm] = useState(TODAY);

  // const contextValueObj = {
  //   setAddDataFormModalShown,
  //   setDateOnForm,
  // };

  let modal;
  // if (addDataFormModalShown) modal = <AddDataForm date={dateOnForm} />;

  return (
    <ExpenseDataProvider>
      <div className={`${style["app__container"]} center`}>
        <SideBar today={TODAY} setPage={setPage} page={page} />

        <MainContent today={TODAY} page={page} />
      </div>
    </ExpenseDataProvider>
  );
}

export default App;

