import { useContext } from "react";
import createConfigObj from "../../../../Others/CreateChartData/createConfigObj";
import AccountSection from "../../../UI/AccountSection/AccountSection";
import ExpenseDataContext from "../../../../store/expenseData/expenseData--context";
import DisplayThemeContext from "../../../../store/displayTheme/displayTheme--context";
import createDateStringFormat from "../../../../Others/CreateDateStringFormat/CreateDateStringFormat";
import style from "./MonthlyInfo.module.css";

const date = new Date();
const firstDay = new Date(date.getFullYear(), date.getMonth(), 1);
const lastDay = new Date(date.getFullYear(), date.getMonth() + 1, 0);

console.log(
  firstDay.getFullYear(),
  firstDay.getMonth() + 1,
  firstDay.getDate()
);
const firstDayStringFormat = new Date(createDateStringFormat(firstDay));
console.log(
  firstDayStringFormat.getFullYear(),
  firstDayStringFormat.getMonth() + 1,
  firstDayStringFormat.getDate()
);

const lastDayStringFormat = new Date(createDateStringFormat(lastDay));

function CalendarInfo(props) {
  const { expenseData, categoryExpense } = useContext(ExpenseDataContext);
  const { displayTheme } = useContext(DisplayThemeContext);

  const accIncome = expenseData
    .filter((expenseData) => {
      const timeNumber = Number(new Date(expenseData.time));

      return (
        timeNumber >= Number(firstDayStringFormat) &&
        timeNumber <= Number(lastDayStringFormat) &&
        expenseData.category === "income"
      );
    })
    .reduce((acc, cur) => acc + Number(cur.price), 0);

  const accExpense = expenseData
    .filter((expenseData) => {
      const timeNumber = Number(new Date(expenseData.time));

      return (
        timeNumber >= Number(firstDayStringFormat) &&
        timeNumber <= Number(lastDayStringFormat) &&
        expenseData.category === "expense"
      );
    })
    .reduce((acc, cur) => acc + Number(cur.price), 0);

  const configBar = createConfigObj(
    "time",
    "30",
    firstDay,
    lastDay,
    expenseData,
    "expense",
    Object.keys(categoryExpense),
    displayTheme
  );

  const configPie = createConfigObj(
    "",
    "30",
    firstDay,
    lastDay,
    expenseData,
    "expense",
    Object.keys(categoryExpense),
    displayTheme
  );

  return (
    <div>
      <AccountSection
        title="Monthly Overview"
        income={accIncome}
        expense={accExpense}
        configBar={configBar}
        configPie={configPie}
      />
    </div>
  );
}

export default CalendarInfo;
