import { useState, Fragment, useEffect } from "react";

import { IoFastFoodSharp } from "react-icons/io";
import { IoIosShirt } from "react-icons/io";
import { GiFamilyHouse } from "react-icons/gi";
import { AiFillCar } from "react-icons/ai";
import { ImBook } from "react-icons/im";
import { IoLogoGameControllerB } from "react-icons/io";

import Title from "../../../UI/Title/Title";
import SubTitle from "../../../UI/SubTitle/SubTitle";
import CalendarList from "./CalendarList";
import Button from "../../../UI/Button";
import ExpenseItem from "./ExpenseItem";
import time from "../../../Others/time";

import { v4 as uuidv4 } from "uuid";

import style from "./Daily.module.css";

const currentDate = new Date();
const CURRENT_DATE = new Date();

let currentTime = time(currentDate);

const EXPENSE_DATA = [
  {
    category: "expense",
    mainCate: "FOOD",
    subCate: "BreakFast",
    time: "18 June, 20",
    description: "Burger",
    money: "110",
  },
  {
    mainCate: "FOOD",
    subCate: "BreakFast",
    time: "18 June, 20",
    description: "Burger",
    money: "110",
  },
  {
    mainCate: "FOOD",
    subCate: "BreakFast",
    time: "18 June, 20",
    description: "Burger",
    money: "110",
  },
  {
    mainCate: "FOOD",
    subCate: "BreakFast",
    time: "18 June, 20",
    description: "Burger",
    money: "110",
  },
  {
    mainCate: "FOOD",
    subCate: "BreakFast",
    time: "18 June, 20",
    description: "Burger",
    money: "110",
  },
  {
    mainCate: "FOOD",
    subCate: "BreakFast",
    time: "18 June, 20",
    description: "Burger",
    money: "110",
  },
  {
    mainCate: "FOOD",
    subCate: "BreakFast",
    time: "18 June, 20",
    description: "Burger",
    money: "110",
  },
  {
    mainCate: "FOOD",
    subCate: "BreakFast",
    time: "18 June, 20",
    description: "Burger",
    money: "110",
  },
  {
    mainCate: "FOOD",
    subCate: "BreakFast",
    time: "18 June, 20",
    description: "Burger",
    money: "110",
  },
  {
    mainCate: "FOOD",
    subCate: "BreakFast",
    time: "18 June, 20",
    description: "Burger",
    money: "110",
  },
];

let index = 0;

function Daily() {
  const [calendarDate, setCalendatDate] = useState(currentTime);
  const [animation, setAnimation] = useState(false);

  useEffect(() => {
    if (index === 0) {
      index++;
      return;
    }

    setAnimation(true);

    const time = setTimeout(() => {
      setAnimation(false);
    }, 300);

    return function cleanUp() {
      clearTimeout(time);
    };
  }, [calendarDate]);

  function incrementDateHandler() {
    currentDate.setDate(currentDate.getDate() + 7);
    currentTime = time(currentDate);
    setCalendatDate(currentTime);
  }

  function dncrementDateHandler() {
    currentDate.setDate(currentDate.getDate() - 7);
    currentTime = time(currentDate);
    setCalendatDate(currentTime);
  }

  let active = null;

  const calendarList = calendarDate.map((time) => {
    if (
      time.month === CURRENT_DATE.getMonth() &&
      time.monthDay === CURRENT_DATE.getDate()
    )
      active = true;
    else active = false;

    return (
      <CalendarList
        key={uuidv4()}
        weekDay={time.weekDay}
        monthDay={time.monthDay}
        active={active}
        animation={animation}
      />
    );
  });

  const expenseItem = EXPENSE_DATA.map((expense, i) => {
    return (
      <Fragment key={uuidv4()}>
        <ExpenseItem
          mainCate={expense.mainCate}
          subCate={expense.subCate}
          time={expense.time}
          description={expense.description}
          money={expense.money}
        />
        {i === EXPENSE_DATA.length - 1 || (
          <hr className={style["item__line"]}></hr>
        )}
      </Fragment>
    );
  });

  return (
    <div className={style.daily}>
      <div className={style["daily__first"]}>
        <Title>daily transection</Title>
        <div className={style["btn__container"]}>
          <Button className={style["btn__addExpense"]}>Add Expense</Button>
          <Button className={style["btn__addIncome"]}>Add Income</Button>
        </div>
      </div>

      <div className={style["calendar__container"]}>
        <Button
          type={"button"}
          className={style["btn__calendar__list"]}
          onClick={dncrementDateHandler}
        >
          {"<"}
        </Button>
        {calendarList}
        <Button
          type={"button"}
          className={style["btn__calendar__list"]}
          onClick={incrementDateHandler}
        >
          {">"}
        </Button>
      </div>

      <div className={style["today__account"]}>
        <div>
          <p className={style["today__account__text"]}>Total Expense</p>
          <p className={style["today__account__number"]}>$110</p>
        </div>
        <div className={style.income}>
          <p className={style["today__account__text"]}>Total Income</p>
          <p className={style["today__account__number"]}>$110</p>
        </div>
        <div>
          <p className={style["today__account__text"]}>Total Net</p>
          <p className={style["today__account__number"]}>$110</p>
        </div>
      </div>

      <div className={style.item}>
        <ul className={style["item__container"]}>{expenseItem}</ul>
      </div>
    </div>
  );
}

export default Daily;
