import { useReducer, useContext, useState } from "react";
import ExpenseDataContext from "../expenseData/expenseData--context";
import SearchListDataContext from "./searchListData--context";

let orginalObj;

function reducer(state, action) {
  switch (action.type) {
    case "ADD": {
      const value = action.value;

      const newObj = { ...state.constraintObj };
      newObj[`${action.id}-${action.value}`] = value;

      return {
        expenseData: reducerHelperFunction(newObj),
        constraintObj: newObj,
      };
    }
    case "REMOVE": {
      const id = `${action.id}-${action.value}`;

      const newObj = { ...state.constraintObj };

      delete newObj[id];

      let newState;
      if (newObj.length === 0) newState = orginalObj;
      else {
        newState = reducerHelperFunction(newObj);
      }

      return { expenseData: newState, constraintObj: newObj };
    }
    default:
      break;
  }
}

function SearchListDataProvider(props) {
  const expenseDataCtx = useContext(ExpenseDataContext);
  const [filteredData, setFilteredData] = useReducer(reducer, {
    constraintObj: {},
    expenseData: expenseDataCtx.EXPENSE_DATA,
  });

  orginalObj = expenseDataCtx.EXPENSE_DATA;

  const SearchListDataContextInitialObject = {
    setFilteredData,
    expenseData: filteredData.expenseData,
  };

  return (
    <SearchListDataContext.Provider value={SearchListDataContextInitialObject}>
      {props.children}
    </SearchListDataContext.Provider>
  );
}

export default SearchListDataProvider;

function filterTime(data, filterArr) {
  if (filterArr.length === 0) return data;
  const date = new Date();

  let dayArr = [];
  let monthArr = [];
  let yearArr = [];
  filterArr.forEach((element) => {
    if (element[0] === "d") dayArr.push(element.split(",")[1]);
    if (element[0] === "m") monthArr.push(element.split(",")[1]);
    if (element[0] === "y") yearArr.push(element.split(",")[1]);
  });

  let dayState = [];
  let monthState = [];
  let yearState = [];

  if (dayArr.length > 0) {
    dayState = data.filter(
      (element) =>
        Number(element.time.slice(8)) >= Number(Math.min(...dayArr)) &&
        Number(element.time.slice(5, 7)) === date.getMonth() + 1
    );
  }

  if (monthArr.length > 0) {
    monthState = data.filter(
      (element) =>
        Number(element.time.slice(5, 7)) >= Number(Math.min(...monthArr)) &&
        Number(element.time.slice(5, 7)) !== date.getMonth() + 1
    );
  }

  if (yearArr.length > 0) {
    yearState = data.filter(
      (element) =>
        Number(element.time.slice(0, 4)) === Number(Math.min(...yearArr))
    );
  }

  return [...dayState, ...monthState, ...yearState];
}

function filterCategory(data, filterArr) {
  if (filterArr.length === 0) return data;

  return data.filter((element) => filterArr.includes(element.mainCate));
}

function filterPrice(data, filterArr) {
  if (filterArr.length === 0) return data;

  let min = Infinity;
  let max = 0;
  let tmpMin;
  let tmpMax;
  const newFilterArr = filterArr.map((arr) => arr.split(","));
  newFilterArr.forEach((element) => {
    [tmpMin, tmpMax] = element;

    if (Number(tmpMin) < min) min = Number(tmpMin);
    if (Number(tmpMax) > max) max = Number(tmpMax);
  });

  return data.filter(
    (element) =>
      Number(element.price) >= Number(min) &&
      Number(element.price) <= Number(max)
  );
}

function reducerHelperFunction(newObj) {
  let currentType;
  let currentKey;
  let timeArr = [];
  let priceArr = [];
  let categoryArr = [];
  for (const key of Object.keys(newObj)) {
    [currentType, currentKey] = key.split("-");

    if (currentType === "category") categoryArr.push(currentKey);
    else if (currentType === "time") timeArr.push(currentKey);
    else priceArr.push(currentKey);
  }

  return filterCategory(
    filterPrice(filterTime(orginalObj, timeArr), priceArr),
    categoryArr
  );
}

