import { useState, Fragment, useEffect } from "react";
import ReactDOM from "react-dom";

import Title from "../SubTitle/SubTitle";
import InputCheckbox from "../InputCheckbox/InputCheckbox";
import Backdrop from "./Backdrop";
import AddDataForm from "../AddDataForm/AddDateForm";
import InputRadio from "../InputRadio/InputRadio";
import Button from "../Button";

import style from "./Modal.module.css";

const EXPENSE_CATEGORY = {
  food: ["breakfast", "brunch", "lunch", "dinner", "snack", "drink"],
  clothing: ["clothes", "pants", "shoes", "accessories", "underwear"],
  housing: [
    "rent",
    "water  bill",
    "electricity bill",
    "gas bill",
    "cable bill",
    "internet bill",
  ],
  transportation: ["gasoline", "parking", "maintenance"],
  education: ["books", "course"],
  entertainment: ["video games", "computer games", "party"],
};

const INCOME_CATEGORY = {
  salary: ["salary", "bonus"],
  stock: ["stock"],
};

function PopupModal(props) {
  return (
    <Backdrop>
      <div className={style.modal}>
        <AddDataForm />
      </div>
    </Backdrop>
  );
}

function Modal(props) {
  return (
    <Fragment>
      {ReactDOM.createPortal(
        <PopupModal setModalShown={props.setModalShown} />,
        document.querySelector("#modal")
      )}
    </Fragment>
  );
}

export default Modal;
