import SearchOptionUI from "../SearchOptionUI";
import createDateFormat from "../../../../../Others/createDateFormat";
import createDateStringFormat from "../../../../../Others/CreateDateStringFormat/CreateDateStringFormat";
const date = new Date();
// console.log(date);
// console.log(date.setMonth(date.getMonth() - 1, 1));
// console.log(date);
const dateToday = new Date(),
  dateYesterDay = new Date(),
  dateThreeDays = new Date(),
  dateLastWeek = new Date(),
  dateMonth = new Date(),
  dateLastMonth = new Date(),
  dateThreeMonths = new Date(),
  dateYear = new Date(),
  dateLastYear = new Date();

dateYesterDay.setDate(dateYesterDay.getDate() - 1);
dateThreeDays.setDate(dateThreeDays.getDate() - 3);

const dateTodayString = createDateStringFormat(dateToday);
console.log(dateTodayString);

const checkboxTime = [
  {
    text: "today",
    value: `day,${date.getDate()},${date.getDate()}`,
    test: ["day", createDateFormat(dateToday), createDateFormat(dateToday)],
  },
  {
    text: "yesterady",
    value: `day,${date.getDate() - 1},${date.getDate() - 1}`,
    test: ["day", createDateFormat(dateYesterDay), createDateFormat(dateToday)],
  },
  {
    text: "three days ago",
    value: `day,${date.getDate()},${date.getDate() - 3}`,
    test: ["day", createDateFormat(dateThreeDays), createDateFormat(dateToday)],
  },
  {
    text: "last week",
    value: `day,${date.getDate()},${date.getDate() - 7}`,
    test: ["week", dateToday, dateLastWeek.setDate(dateLastWeek.getDate() - 7)],
  },
  {
    text: "this month",
    value: `month,${date.getMonth() + 1},${date.getMonth() + 1}`,
    test: [
      "week",
      dateMonth.setDate(1),
      dateMonth.setMonth(dateMonth.getMonth() + 1, 0),
    ],
  },
  {
    text: "last month",
    value: `month,${date.getMonth()},${date.getMonth()}`,
    test: [
      "week",
      dateLastMonth.setMonth(dateLastMonth.getMonth() - 1, 1),
      dateLastMonth.setMonth(0),
    ],
  },
  {
    text: "three months ago",
    value: `month,${date.getMonth()},${date.getMonth() - 2}`,
    test: [
      "week",
      new Date(dateToday.getFullYear(), dateToday.getMonth() - 3, 1),
      new Date(dateToday.getFullYear(), dateToday.getMonth(), 0),
    ],
  },
  {
    text: "this year",
    value: `year,${date.getFullYear()}`,
    test: [
      "year",
      new Date(dateToday.getFullYear(), dateToday.getMonth() - 3, 1),
      new Date(dateToday.getFullYear(), dateToday.getMonth(), 0),
    ],
  },
  { text: "last year", value: `year,${date.getFullYear() - 1}` },
];

console.log(checkboxTime);

function SearchOptionTime() {
  return (
    <SearchOptionUI dataID="time" label="time" checkboxItem={checkboxTime} />
  );
}

export default SearchOptionTime;
