import { useContext } from "react";
import AccountSection from "../../../UI/AccountSection/AccountSection";
import ExpenseDataContext from "../../../../store/expenseData/expenseData--context";
import DisplayThemeContext from "../../../../store/displayTheme/displayTheme--context";
import createWeeklyData from "../../../../Others/createWeeklyData";
import createConfigObj from "../../../../Others/CreateChartData/createConfigObj";
import createDateStringFormat from "../../../../Others/CreateDateStringFormat/CreateDateStringFormat";
import style from "./Weekly.module.css";

const labels = ["SUN", "MON", "TUE", "WED", "THR", "FRI", "SAT"];

const weeklyDataArr = createWeeklyData(new Date());
const first = weeklyDataArr[0];
const last = weeklyDataArr[weeklyDataArr.length - 1];

const startingDate = createDateStringFormat(first.dateObj);
const endingDate = createDateStringFormat(last.dateObj);

function Weekly() {
  const { expenseData, categoryExpense } = useContext(ExpenseDataContext);
  const { displayTheme } = useContext(DisplayThemeContext);

  const configBar = createConfigObj(
    "time",
    "7",
    startingDate,
    endingDate,
    expenseData,
    "expense",
    Object.keys(categoryExpense),
    displayTheme
  );

  const configPie = createConfigObj(
    "",
    "7",
    startingDate,
    endingDate,
    expenseData,
    "expense",
    Object.keys(categoryExpense),
    displayTheme
  );

  configBar.data.labels = labels;

  console.log(last.dateObj);

  const accIncome = expenseData
    .filter((expenseData) => {
      const year = expenseData.time.slice(0, 4);
      const month = expenseData.time.slice(5, 7);
      const day = expenseData.time.slice(8, 10);

      // 2021-12-29
      const timeNumber = Number(new Date(expenseData.time));

      return (
        timeNumber <= Number(last.dateObj) &&
        timeNumber >= Number(first.dateObj) &&
        expenseData.category === "income"
      );
    })
    .reduce((acc, cur) => acc + Number(cur.price), 0);

  const accExpense = expenseData
    .filter((expenseData) => {
      const timeNumber = Number(new Date(expenseData.time));

      return (
        timeNumber <= Number(new Date(last.string)) &&
        timeNumber >= Number(new Date(first.string)) &&
        expenseData.category === "expense"
      );
    })
    .reduce((acc, cur) => acc + Number(cur.price), 0);

  return (
    <div className={style.weekly}>
      <AccountSection
        title="Weekly Overview"
        income={accIncome}
        expense={accExpense}
        configBar={configBar}
        configPie={configPie}
      />
    </div>
  );
}

export default Weekly;
