import { useRef, useEffect, useContext } from "react";
import ExpenseDataContext from "../../../../store/expenseData/expenseData--context";
import style from "./ChartMain.module.css";
import Chart from "chart.js/auto";

const dateOptObj = { month: "short" };

function ChartMain(props) {
  const chartRef = useRef(null);
  const { EXPENSE_DATA } = useContext(ExpenseDataContext);

  let labels = [],
    labels1 = [],
    data = [];

  if (props.chartData) {
    const standardLabels = createLabelsArr(
      props.chartData.timeDuration,
      props.chartData.startingDate
    );

    // labels = standardLabels.map((label) => )

    // if (
    //   props.chartData.timeDuration === "6" ||
    //   props.chartData.timeDuration === "12"
    // ) {
    //   labels1 = labels.map((label) =>
    //     new Intl.DateTimeFormat("en-US", dateOptObj).format(
    //       new Date(2020, label.slice(0, 2) - 1, label.slice(2) - 0)
    //     )
    //   );
    // }

    data = createDataArr(
      standardLabels,
      EXPENSE_DATA,
      props.chartData?.startingDate,
      props.chartData?.mainCategory,
      props.chartData?.subCategory,
      props.chartData?.timeDuration
    );
  }

  const dataObj = {
    labels: labels,
    datasets: [
      {
        label: "My First Dataset",
        data: data,
        backgroundColor: [
          "rgba(255, 99, 132, 0.2)",
          "rgba(255, 159, 64, 0.2)",
          "rgba(255, 205, 86, 0.2)",
          "rgba(75, 192, 192, 0.2)",
          "rgba(54, 162, 235, 0.2)",
          "rgba(153, 102, 255, 0.2)",
          "rgba(201, 203, 207, 0.2)",
        ],
        borderColor: [
          "rgb(255, 99, 132)",
          "rgb(255, 159, 64)",
          "rgb(255, 205, 86)",
          "rgb(75, 192, 192)",
          "rgb(54, 162, 235)",
          "rgb(153, 102, 255)",
          "rgb(201, 203, 207)",
        ],
        borderWidth: 1,
      },
    ],
  };

  const config = {
    type: "bar",
    data: dataObj,
    options: {
      scales: {
        y: {
          beginAtZero: true,
        },
      },
    },
  };

  // useEffect(() => {
  //   const chart = new Chart(chartRef.current, config);

  //   return function cleanUp() {
  //     chart.destroy();
  //   };
  // }, [labels]);

  return (
    <div className={style.chartMain}>
      <div>{/* <canvas ref={chartRef}></canvas> */}</div>
    </div>
  );
}

export default ChartMain;

function createDataArr(
  standardLabels,
  expensData,
  start,
  mainCategory,
  subCategory,
  timeDuration
) {
  // const year = new Date(start).getFullYear();
  // const startingDate = `${year}-${labels[0]?.slice(0, 2)}-${labels[0]?.slice(
  //   2
  // )}`;
  // const endingDate = `${year}-${labels[labels.length - 1]?.slice(
  //   0,
  //   2
  // )}-${labels[labels.length - 1]?.slice(2)}`;

  const numStartingDate = Number(new Date(standardLabels[0]));
  const numEndingDate = Number(
    new Date(standardLabels[standardLabels.length - 1])
  );
  console.log(endingDate);

  const filteredData = expensData
    .filter(
      (element) =>
        Number(new Date(element.time)) >= numStartingDate &&
        Number(new Date(element.time)) <= numEndingDate
    )
    .sort(
      (elementA, elementB) =>
        Number(new Date(elementA.time)) - Number(new Date(elementB.time))
    );

  console.log(filteredData);
  const data = [];
  // month base
  if (timeDuration === "6" || timeDuration === "12") {
    if (mainCategory === "income" && subCategory.length === 3) {
      const labelsLength = labels.length;
      let indexLabel = 0;
      let indexData = 0;
      let indexCurrentDate = true;
      let currentMonth;
      let currentAmount = 0;

      while (indexLabel < labelsLength) {
        // keep adding if it's still at current date(time)
        while (indexCurrentDate) {
          currentMonth = new Intl.DateTimeFormat("en-US", dateOptObj).format(
            new Date(filteredData[indexData]?.time)
          );
          // check date(time)
          if (currentMonth === labels1[indexLabel]) {
            // check main category
            if (filteredData[indexData].category === "income") {
              currentAmount += Number(filteredData[indexData].price);
            }

            indexData++;
          } else indexCurrentDate = false;
        }

        data.push(currentAmount);

        currentAmount = 0;
        indexCurrentDate = true;
        indexLabel++;
      }
    }

    if (mainCategory === "expense" && subCategory.length === 6) {
      const labelsLength = labels.length;
      let indexLabel = 0;
      let indexData = 0;
      let indexCurrentDate = true;
      let currentMonth;
      let currentAmount = 0;

      while (indexLabel < labelsLength) {
        // keep adding if it's still at current date(time)
        while (indexCurrentDate) {
          console.log(filteredData);
          if (!filteredData[indexData]?.time)
            currentMonth = new Intl.DateTimeFormat("en-US", dateOptObj).format(
              new Date(filteredData[indexData]?.time)
            );
          console.log(currentMonth);

          if (currentMonth === labels1[indexLabel]) {
            // check main category
            if (filteredData[indexData].category === "income") {
              currentAmount += Number(filteredData[indexData].price);
            }

            indexData++;
          } else indexCurrentDate = false;
        }

        data.push(currentAmount);

        currentAmount = 0;
        indexCurrentDate = true;
        indexLabel++;
      }
    }
  }
  // day base
  else {
    if (mainCategory === "income" && subCategory.length === 3) {
      const labelsLength = labels.length;
      let indexLabel = 0;
      let indexData = 0;
      let indexCurrentDate = true;
      let currentDate;
      let currentAmount = 0;

      while (indexLabel < labelsLength) {
        currentDate = `${year}-${labels[indexLabel].slice(0, 2)}-${labels[
          indexLabel
        ].slice(2)}`;

        // keep adding if it's still at current date(time)
        if (indexData < filteredData.length - 1) {
          while (indexCurrentDate) {
            // check date(time)
            if (filteredData[indexData].time === currentDate) {
              // check main category
              if (filteredData[indexData].category === "income") {
                currentAmount += Number(filteredData[indexData].price);
              }

              indexData++;
            } else indexCurrentDate = false;
          }
        }

        data.push(currentAmount);

        currentAmount = 0;
        indexCurrentDate = true;
        indexLabel++;
      }
    }

    if (mainCategory === "expense" && subCategory.length === 6) {
      const labelsLength = labels.length;
      let indexLabel = 0;
      let indexData = 0;
      let indexCurrentDate = true;
      let currentDate;
      let currentAmount = 0;

      while (indexLabel < labelsLength) {
        currentDate = `${year}-${labels[indexLabel].slice(0, 2)}-${labels[
          indexLabel
        ].slice(2)}`;

        // keep adding if it's still at current date(time)

        while (indexCurrentDate) {
          // check date(time)
          if (filteredData[indexData]?.time === currentDate) {
            // check main category
            if (filteredData[indexData].category === "expense") {
              currentAmount += Number(filteredData[indexData].price);
            }

            indexData++;
          } else indexCurrentDate = false;
        }

        data.push(currentAmount);

        currentAmount = 0;
        indexCurrentDate = true;
        indexLabel++;
      }
    }
  }
  // console.log(filteredData);

  console.log(labels);
  console.log(data);
  // console.log(data);
  return data;
}

function createLabelsArr(duration, startingDate) {
  const today = new Date(startingDate);

  let labels = [];

  // one week
  if (duration === "7") {
    createLabelsArrHelper(labels, 7, 1, "day", today);
  }

  if (duration === "14") {
    createLabelsArrHelper(labels, 14, 1, "day", today);
  }

  if (duration === "30") {
    createLabelsArrHelper(labels, 30, 1, "day", today);
  }

  if (duration === "90") {
    createLabelsArrHelper(labels, 30, 3, "day", today);
  }

  if (duration === "6") {
    createLabelsArrHelper(labels, 6, 1, "month", today);
  }

  if (duration === "12") {
    createLabelsArrHelper(labels, 12, 1, "month", today);
  }

  return labels;
}

function createLabelsArrHelper(
  labels,
  duration,
  incrementAmount,
  dayMonth,
  today
) {
  let tmpDay = today;
  let day;
  let month;
  let year = today.getFullYear();
  let changeYear = false;

  // if it's increment by month, the day is never change
  // we could put it outisde for-loop
  if (dayMonth === "month") {
    if (tmpDay.getDate() < 10) day = `0${tmpDay.getDate()}`;
    else day = tmpDay.getDate();
  }

  for (let i = 0; i < duration; i++) {
    // incremenet by month
    if (dayMonth === "month")
      tmpDay.setMonth(tmpDay.getMonth() + incrementAmount);

    // increment by day
    if (dayMonth === "day") {
      tmpDay.setDate(tmpDay.getDate() + incrementAmount);

      // day will change, so put this inside for-loop
      if (tmpDay.getDate() < 10) day = `0${tmpDay.getDate()}`;
      else day = tmpDay.getDate();
    }

    if (tmpDay.getMonth() + 1 < 10) month = `0${tmpDay.getMonth() + 1}`;
    else month = tmpDay.getMonth() + 1;

    // increment year
    if (changeYear) {
      year++;
      changeYear = false;
    }
    if (tmpDay.getMonth() === 11) changeYear = true;

    labels.push(`${year}-${month}-${day}`);
  }
}
