import SearchOptionUI from "../SearchOptionUI";
const date = new Date();
console.log(date);
date.setDate();

console.log(date);
const dateToday = new Date(),
  dateYesterDay = new Date(),
  dateThreeDays = new Date(),
  dateLastWeek = new Date(),
  dateMonth = new Date(),
  dateThreeMonths = new Date(),
  dateYear = new Date(),
  dateLastYear = new Date();

const checkboxTime = [
  {
    text: "today",
    value: `day,${date.getDate()},${date.getDate()}`,
    test: ["day", dateToday, dateToday],
  },
  {
    text: "yesterady",
    value: `day,${date.getDate() - 1},${date.getDate() - 1}`,
    test: [
      "day",
      dateToday,
      dateYesterDay.setDate(dateYesterDay.getDate() - 1),
    ],
  },
  {
    text: "three days ago",
    value: `day,${date.getDate()},${date.getDate() - 3}`,
    test: [
      "day",
      dateToday,
      dateThreeDays.setDate(dateThreeDays.getDate() - 3),
    ],
  },
  {
    text: "last week",
    value: `day,${date.getDate()},${date.getDate() - 7}`,
    test: ["week", dateToday, dateLastWeek.setDate(dateLastWeek.getDate() - 7)],
  },
  {
    text: "this month",
    value: `month,${date.getMonth() + 1},${date.getMonth() + 1}`,
    test: [
      "week",
      dateMonth.setDate(1),
      new Date(dateMonth.getFullYear(), dateMonth.getMonth() + 1, 0),
    ],
  },
  {
    text: "last month",
    value: `month,${date.getMonth()},${date.getMonth()}`,
    test: [
      "week",
      new Date(dateToday.getFullYear(), dateToday.getMonth() - 1, 1),
      new Date(dateToday.getFullYear(), dateToday.getMonth(), 0),
    ],
  },
  {
    text: "three months ago",
    value: `month,${date.getMonth()},${date.getMonth() - 2}`,
    test: [
      "week",
      new Date(dateToday.getFullYear(), dateToday.getMonth() - 3, 1),
      new Date(dateToday.getFullYear(), dateToday.getMonth(), 0),
    ],
  },
  { text: "this year", value: `year,${date.getFullYear()}` },
  { text: "last year", value: `year,${date.getFullYear() - 1}` },
];

function SearchOptionTime() {
  return (
    <SearchOptionUI dataID="time" label="time" checkboxItem={checkboxTime} />
  );
}

export default SearchOptionTime;
