import { useRef, useEffect, useState } from "react";
import Title from "../../../UI/Title/Title";
import style from "./AccountSmallChart.module.css";
import Card from "../../../UI/Card/Card";
import Chart from "chart.js/auto";

function AccountSmallChart(props) {
  const chartRef = useRef(null);
  return (
    <Card className={style["chart__section"]}>
      <div className={style["title__section"]}>
        <Title>Chart</Title>
        <select className={style.select}>
          <option value="0">line chart</option>
          <option value="1">area chart</option>
          <option value="2">bar chart</option>
          <option value="3">pie chart</option>
        </select>
      </div>
      <div className={style["chart__container"]}>
        <canvas
          ref={chartRef}
          className={
            chartState === "3" ? `${style["chart--pie"]}` : `${style["chart"]}`
          }
          height="200"
          width="auto"
        ></canvas>
      </div>
    </Card>
  );
}

export default AccountSmallChart;
