function createLabelsArr(duration, startingDate) {
  const today = new Date(startingDate);

  let labels = [];

  // one week
  if (duration === "7") {
    createLabelsArrHelper(labels, 7, 1, "day", today);
  }

  if (duration === "14") {
    createLabelsArrHelper(labels, 14, 1, "day", today);
  }

  if (duration === "30") {
    createLabelsArrHelper(labels, 30, 1, "day", today);
  }

  if (duration === "90") {
    createLabelsArrHelper(labels, 30, 3, "day", today);
  }

  if (duration === "6") {
    createLabelsArrHelper(labels, 6, 1, "month", today);
  }

  if (duration === "12") {
    createLabelsArrHelper(labels, 12, 1, "month", today);
  }

  return labels;
}

function createLabelsArrHelper(
  labels,
  duration,
  incrementAmount,
  dayMonth,
  today
) {
  let tmpDay = today;
  let day;
  let month;
  let year = today.getFullYear();
  let changeYear = false;

  // if it's increment by month, the day is never change
  // we could put it outisde for-loop
  if (dayMonth === "month") {
    if (tmpDay.getDate() < 10) day = `0${tmpDay.getDate()}`;
    else day = tmpDay.getDate();
  }

  for (let i = 0; i < duration; i++) {
    // incremenet by month
    if (dayMonth === "month")
      tmpDay.setMonth(tmpDay.getMonth() + incrementAmount);

    // increment by day
    if (dayMonth === "day") {
      tmpDay.setDate(tmpDay.getDate() + incrementAmount);

      // day will change, so put this inside for-loop
      if (tmpDay.getDate() < 10) day = `0${tmpDay.getDate()}`;
      else day = tmpDay.getDate();
    }

    if (tmpDay.getMonth() + 1 < 10) month = `0${tmpDay.getMonth() + 1}`;
    else month = tmpDay.getMonth() + 1;

    // increment year
    if (changeYear) {
      year++;
      changeYear = false;
    }
    if (tmpDay.getMonth() === 11) changeYear = true;

    labels.push(`${year}-${month}-${day}`);
  }
}
