import { useState } from "react";

import style from "./ChartOption.module.css";
import Button from "../../../UI/Button";
import OptionRadioMain from "./OptionRadioMain/OptionRadioMain";
import OptionTime from "./OptionTime/OptionTime";
import OptionRadioSub from "./OptionRadioSub/OptionRadioSub";
import OptionCheckboxExpense from "./OptionCheckbox/OptionCheckboxExpense";
import OptionCheckboxIncome from "./OptionCheckbox/OptionCheckBoxIncome";

function ChartOption(props) {
  const [optionData, setOptionData] = useState();

  function OptionRadioHandler(e) {
    console.log(e.target.value);
    console.log("aaa");
  }

  let checkboxContent = "";

  if (optionData === "income") checkboxContent = <OptionCheckboxIncome />;
  if (optionData === "expense") checkboxContent = <OptionCheckboxExpense />;

  return (
    <form className={style.chartOption}>
      <div>
        <h6 className={style["chartOption__title"]}>Analyize By</h6>

        <OptionRadioMain OnOptionRadioHandler={OptionRadioHandler} />

        <div>
          <OptionTime />
          <OptionRadioSub setOptionData={setOptionData} />
          {checkboxContent}
        </div>
      </div>

      <Button>Show Chart</Button>
    </form>
  );
}

export default ChartOption;
