import { useContext } from "react";
import AccountSection from "../../../UI/AccountSection/AccountSection";
import ExpenseDataContext from "../../../../store/expenseData/expenseData--context";
import createAccumulatedData from "../../../Others/CreateSmallChartData/createAccumulatedData";
import createIndividualData from "../../../Others/CreateSmallChartData/createIndividualData";
import style from "./Weekly.module.css";

// const currentTime = new Date();

// const optionDemo = {
//   // hour: 'numeric',
//   // minute: 'numeric',
//   day: "numeric",
//   month: "short", // long, numeric, 2-digit
//   year: "numeric", // 2-digit
//   weekday: "short", // long, numeric, 2-digit
// };

// const time = new Intl.DateTimeFormat("en-US", optionDemo).format(currentTime);

const labels = ["MON", "TUE", "WED", "THR", "FRI", "SUN", "SAT"];

const configLine = {
  type: "line",
  data: {
    labels: labels,
    datasets: [
      {
        label: "My First Dataset",
        data: [65, 59, 80, 81, 56, 55, 40],
        backgroundColor: ["rgba(54, 162, 235, 0.2)"],
        borderColor: ["rgb(54, 162, 235)"],
        borderWidth: 1,
        pointBackgroundColor: ["rgb(54, 162, 235)"],
      },
    ],
  },
  options: {
    elements: {
      line: {
        tension: 0.1,
      },
    },
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  },
};

const configPie = {
  type: "pie",
  data: {
    labels: ["Red", "Blue", "Yellow"],
    datasets: [
      {
        label: "My First Dataset",
        data: [300, 50, 100],
        backgroundColor: [
          "rgb(255, 99, 132)",
          "rgb(54, 162, 235)",
          "rgb(255, 205, 86)",
        ],
        hoverOffset: 4,
      },
    ],
  },
};

const chartConfigArr = [configLine, configPie];

const today = new Date(); // get current date
const first = today.getDate() - today.getDay(); // First day is the day of the month - the day of the week
const last = first + 6; // last day is the first day + 6

const labelsForData = [];
const year = today.getFullYear();
const month =
  today.getMonth() + 1 > 10 ? today.getMonth() + 1 : `0${today.getMonth() + 1}`;
for (let i = 0; i < 7; i++) {
  labelsForData.push(
    `${year}-${month}-${first + i > 10 ? first + i : `0${first}`}`
  );
}

function Weekly() {
  const { expenseData } = useContext(ExpenseDataContext);

  const filteredData = expenseData.filter(
    (element) =>
      new Date(element.time).getFullYear() === new Date().getFullYear() &&
      new Date(element.time).getMonth() === new Date().getMonth() &&
      new Date(element.time).getDate() <= last &&
      new Date(element.time).getDate() >= first &&
      element.category === "expense"
  );

  const dataLine = createIndividualData(labelsForData, filteredData);

  return (
    <div className={style.weekly}>
      <AccountSection
        title="Weekly Overview"
        income="1000"
        expense="600"
        configArr={chartConfigArr}
      />
    </div>
  );
}

export default Weekly;

