import style from "./InputRadio.module.css";

function InputRadio(props) {
  const containerClassName = props.classContainer
    ? `${props.classContainer} ${style["radio__container"]}`
    : `${style["radio__container"]}`;

  const checkClassName = props.classCheck
    ? `${props.classCheck} transition--25`
    : `transition--25`;

  const insideClassName = props.classInside
    ? `${props.classInside} ${style.inside} transition--25`
    : `${style.inside} transition--25`;

  return (
    <div className={containerClassName}>
      <input
        checked={props.checked}
        defaultChecked={props.defaultChecked}
        onChange={props.onChange}
        type="radio"
        id={props.id}
        name={props.name}
        value={props.value}
        className={
          props.classInput
            ? `${props.classInput} ${style.input}`
            : `${style.input}`
        }
      />
      <div className={checkClassName}>
        <div className={insideClassName}></div>
      </div>

      <label className={props.classLabel} htmlFor={props.id}>
        {props.label}
      </label>
    </div>
  );
}

export default InputRadio;
