import { useContext } from "react";
import useEditModal from "../../components/Others/Custom/useEditModal";
import EditModalContext from "./editModal--context";

function EditModalProvider(props) {
  const [, setEditModal] = useEditModal(false);

  return (
    <EditModalContext.Provider value={setEditModal}>
      {props.children}
    </EditModalContext.Provider>
  );
}

export default EditModalProvider;
