import { React } from "react";
import { v4 as uuidv4 } from "uuid";
import style from "./createCalendar.module.css";

const DATE = new Date();

const MONTH = DATE.getMonth();
const TODAY = DATE.getDate();

const cacheObj = {};

function createCalendar(
  date,
  { setExpenseListModalShown, getData },
  expenseData
) {
  let key = date.getFullYear() + "" + (date.getMonth() + 1);
  if (key.length < 6) {
    key = date.getFullYear() + "0" + (date.getMonth() + 1);
  }

  if (!cacheObj[key]) {
    addCacheData(cacheObj, date, key, expenseData);
  }

  console.log(expenseData);

  const res = [];

  function showModalHandler(e) {
    const rawData = e.target.dataset.id;

    if (!rawData) return;

    const data = `${rawData.slice(0, 4)}-${rawData.slice(4, 6)}-${rawData.slice(
      6
    )}`;

    const allDataArr = expenseData.filter((element) => {
      return element.time === data;
    });

    if (allDataArr.length === 0) getData(rawData);
    else getData(allDataArr);

    setExpenseListModalShown(true);
  }

  // last day of current month
  // 0 gives us the last day of previous month
  // + 1 makes to be last day fo current month
  // get date gives us the number type
  const lastDay = new Date(
    date.getFullYear(),
    date.getMonth() + 1,
    0
  ).getDate();

  const prevLastDay = new Date(
    date.getFullYear(),
    date.getMonth(),
    0
  ).getDate(); // last day of previous month

  // last day of the week (current month)
  const lastDayIndex = new Date(
    date.getFullYear(),
    date.getMonth() + 1,
    0
  ).getDay();

  // first day of the week (current month)
  // const firstDayIndex = date.getDay();
  const firstDayIndex = new Date(
    date.getFullYear(),
    date.getMonth(),
    1
  ).getDay();

  const nextDays = 7 - lastDayIndex - 1;

  for (let i = firstDayIndex; i > 0; i--) {
    res.push(
      <div className={style.negative} key={uuidv4()}>
        {prevLastDay - i + 1}
      </div>
    );
  }

  for (let i = 1; i <= lastDay; i++) {
    if (i === TODAY && date.getMonth() === MONTH) {
      res.push(
        checkDot(
          i >= 10 ? i + "" : "0" + i,
          true,
          cacheObj[key][0],
          cacheObj[key][1],
          showModalHandler,
          key
        )
      );
    } else
      res.push(
        checkDot(
          i >= 10 ? i + "" : "0" + i,
          false,
          cacheObj[key][0],
          cacheObj[key][1],
          showModalHandler,
          key
        )
      );
  }

  for (let i = 1; i <= nextDays; i++) {
    res.push(
      <div className={style.negative} key={uuidv4()}>
        {i}
      </div>
    );
  }

  return res;
}

export default createCalendar;

///////////////////////////////////////////////////////////////
///////////////////////////////////////////////////////////////
///////////////////////////////////////////////////////////////
function checkDot(
  i,
  current,
  TIME_EXPENSE,
  TIME_INCOME,
  showModalHandler,
  key
) {
  const numberI = Number(i);

  if (TIME_EXPENSE.includes(numberI)) {
    if (TIME_INCOME.includes(numberI)) {
      return (
        <div
          onClick={showModalHandler}
          className={current ? style.current : ""}
          key={uuidv4()}
          data-id={key + i}
        >
          {i}
          <div className={style["dot__container"]}>
            <span className={`${style["dot--blue"]} ${style.dot}`}></span>
            <span className={`${style["dot--pink"]} ${style.dot}`}></span>
          </div>
        </div>
      );
    } else {
      return (
        <div
          onClick={showModalHandler}
          className={current ? style.current : ""}
          key={uuidv4()}
          data-id={key + i}
        >
          {i}
          <div className={style["dot__container"]}>
            <span className={`${style["dot--blue"]} ${style.dot}`}></span>
          </div>
        </div>
      );
    }
  } else if (TIME_INCOME.includes(numberI)) {
    return (
      <div
        onClick={showModalHandler}
        className={current ? style.current : ""}
        key={uuidv4()}
        data-id={key + i}
      >
        {i}
        <div className={style["dot__container"]}>
          <span className={`${style["dot--pink"]} ${style.dot}`}></span>
        </div>
      </div>
    );
  } else {
    return (
      <div
        onClick={showModalHandler}
        className={current ? style.current : ""}
        key={uuidv4()}
        data-id={key + i}
      >
        {i}
      </div>
    );
  }
}

function addCacheData(cacheObj, date, key, expenseData) {
  cacheObj[key] = [];

  const TIME_EXPENSE = expenseData
    .filter((element) => {
      if (
        Number(element.time.slice(0, 4)) === date.getFullYear() &&
        Number(element.time.slice(5, 7)) === date.getMonth() + 1 &&
        element.category === "expense"
      )
        return true;
    })
    .map((element) => Number(element.time.slice(8)));

  cacheObj[key].push(TIME_EXPENSE);

  const TIME_INCOME = expenseData
    .filter((element) => {
      if (
        Number(element.time.slice(0, 4)) === date.getFullYear() &&
        Number(element.time.slice(5, 7)) === date.getMonth() + 1 &&
        element.category === "income"
      )
        return true;
    })
    .map((element) => Number(element.time.slice(8)));

  cacheObj[key].push(TIME_INCOME);
}
