import createWeeklyData from "./createWeeklyData";
import createDateStringFormat from "./CreateDateStringFormat/CreateDateStringFormat";
import createConfigObj from "./CreateChartData/createConfigObj";
import timeObj from "../components/assets/timeObj/timeObj";

const labels = ["SUN", "MON", "TUE", "WED", "THR", "FRI", "SAT"];
const weeklyDataArr = createWeeklyData(timeObj.TODAY);

const first = weeklyDataArr[0];
const last = weeklyDataArr[weeklyDataArr.length - 1];

const startingDate = createDateStringFormat(first.dateObj);
const endingDate = createDateStringFormat(last.dateObj);

function createSmallChartData(expenseData, categoryExpense, displayTheme) {
  const configBar = createConfigObj(
    "time",
    "7",
    startingDate,
    endingDate,
    expenseData,
    "expense",
    Object.keys(categoryExpense),
    displayTheme
  );

  const configPie = createConfigObj(
    "",
    "7",
    startingDate,
    endingDate,
    expenseData,
    "expense",
    Object.keys(categoryExpense),
    displayTheme
  );

  configBar.data.labels = labels;

  return [configBar, configPie];
}

export default createSmallChartData;
