import { useEffect, useRef } from "react";

import AccountInfo from "../../../UI/AccountInfo/AccountInfo";

import Chart from "chart.js/auto";

const labels = ["MON", "TUE", "WED", "THR", "FRI", "SUN", "SAT"];
const data = {
  labels: labels,
  datasets: [
    {
      label: "My First Dataset",
      data: [65, 59, 80, 81, 56, 55, 40],
      backgroundColor: [
        "rgba(255, 99, 132, 0.2)",
        "rgba(255, 159, 64, 0.2)",
        "rgba(255, 205, 86, 0.2)",
        "rgba(75, 192, 192, 0.2)",
        "rgba(54, 162, 235, 0.2)",
        "rgba(153, 102, 255, 0.2)",
        "rgba(201, 203, 207, 0.2)",
      ],
      borderColor: [
        "rgb(255, 99, 132)",
        "rgb(255, 159, 64)",
        "rgb(255, 205, 86)",
        "rgb(75, 192, 192)",
        "rgb(54, 162, 235)",
        "rgb(153, 102, 255)",
        "rgb(201, 203, 207)",
      ],
      borderWidth: 1,
    },
  ],
};

const config = {
  type: "bar",
  data: data,
  options: {
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  },
};

const data1 = {
  labels: ["Red", "Blue", "Yellow"],
  datasets: [
    {
      label: "My First Dataset",
      data: [300, 50, 100],
      backgroundColor: [
        "rgb(255, 99, 132)",
        "rgb(54, 162, 235)",
        "rgb(255, 205, 86)",
      ],
      hoverOffset: 4,
    },
  ],
};

const config1 = {
  type: "doughnut",
  data: data1,
};

function CalendarInfo(props) {
  useEffect(() => {
    const chart = new Chart(chartRef.current, config);

    const chart1 = new Chart(chartRef1.current, config1);

    return function cleanUp() {
      chart.destroy();
      chart1.destroy();
    };
  }, []);
  return (
    <div>
      <AccountInfo
        title="Monthly Overview"
        income="1000"
        expense="600"
        net="400"
      />
      <div>
        <h6>Weekly Overview</h6>
        <div>
          <canvas height="200" width="auto"></canvas>
        </div>
        <div>
          <canvas height="200" width="auto"></canvas>
        </div>
      </div>
    </div>
  );
}

export default CalendarInfo;
