import { useState, Fragment } from "react";

import MainContent from "./components/MainContent/MainContent";
import SideBar from "./components/SideBar/SideBar";
import AddDataForm from "./components/UI/AddDataForm/AddDateForm";
import Modal from "./components/UI/Modal/Modal";
import FormContext from "./store/formShown/form--context";
import ExpenseDataContext from "./store/expenseData/expenseData--context";

import style from "./App.module.css";

const TODAY = new Date();

function App() {
  const [page, setPage] = useState(0);
  const [modalShown, setModalShown] = useState(false);

  return (
    <ExpenseDataContext.Provider>
      <FormContext.Provider value={{ setModalShown }}>
        {modalShown && <Modal content={<AddDataForm />} />}

        <div className={`${style["app__container"]} center`}>
          <SideBar today={TODAY} setPage={setPage} page={page} />

          <MainContent
            setModalShown={setModalShown}
            today={TODAY}
            page={page}
          />
        </div>
      </FormContext.Provider>
    </ExpenseDataContext.Provider>
  );
}

export default App;

